const Nav = () => {
  const [stuck, setStuck] = useState(false);
  const ref = useRef<HTMLDivElement>(null!);

  useEffect(() => {
    const topMargin = 120;

    const observer = new IntersectionObserver(
      ([e]) => {
        setStuck(e.boundingClientRect.top < topMargin);
      },
      {threshold: [1], rootMargin: `-${topMargin}px 0px 0px 0px`},
    );

    observer.observe(ref.current);
    return () => observer.disconnect();
  }, []);

  return (
    <div
      tw="sticky top-[-1px] border-t border-t-transparent max-w-[840px] flex mx-auto mt-5 pt-4"
      ref={ref}
    >
      <div tw="[flex-basis: 282px]" />
      <motion.div
        tw="max-w-[555px] [backdrop-filter: blur(10px)] -ml-6 rounded-full"
        animate={stuck ? {marginLeft: '-80px'} : {marginLeft: '-24px'}}
        transition={{type: 'tween', ease: 'easeOut'}}
      >
        <div tw="items-center flex">
          <motion.div
            tw="text-center font-bold text-gold-12  overflow-hidden whitespace-nowrap mt-[-12px]"
            animate={
              stuck
                ? {
                    width: 'auto',
                    opacity: 1,
                    transition: {
                      type: 'tween',
                      ease: 'easeOut',
                      opacity: {
                        times: [0, 0.3],
                      },
                    },
                  }
                : {
                    width: '0px',
                    opacity: 0,
                    transition: {
                      type: 'tween',
                      ease: 'easeOut',
                      opacity: {
                        times: [0, 0.7],
                      },
                    },
                  }
            }
          >
            <div tw="mr-4 pl-6">jude hunter</div>
          </motion.div>
          <ul tw="items-center gap-x-8 px-6 py-1.5 border border-gold-4 flex rounded-full">
            <NavItem>about</NavItem>
            <NavItem>resumÃ©</NavItem>
            <NavItem>articles</NavItem>
            <NavItem>newsletter</NavItem>
          </ul>
        </div>
      </motion.div>
    </div>
  );
};

